</template>

<script>
    export default {
        name: "RpsComponent",

        props: [
            'rps_id',
            'computerscore',
            'humanscore'
        ],

        data() {
            return {
                nbsamplelength: 2,
                ownorall: 'own',
                active: true,
                animating: false,
                calculatedguess: null,
                computerguess: null,
                nextguess: null,
                computerwins: 0,
                humanwins: 0,
                humanresult: '',
                humanguess: null,
                result: '',
                guiresult: {
                    rock: '',
                    paper: '',
                    scissors: ''
                }
            }
        },

        mounted() {
            this.guessRPS();
            this.computerwins = this.computerscore;
            this.humanwins = this.humanscore;
        },

        computed: {
            scissorBack() {
                if(this.animating && this.guiresult.scissors == 'tie'){return 'tie'}
                if(this.animating && this.humanguess == 3) { return 'humanguess' }
                if(this.animating && this.computerguess == 3) { return 'computerguess' }
            },

            paperBack() {
                if(this.animating && this.guiresult.paper == 'tie'){return 'tie'}
                if(this.animating && this.humanguess == 2) { return 'humanguess' }
                if(this.animating && this.computerguess == 2) { return 'computerguess' }
            },

            rockBack() {
                if(this.animating && this.guiresult.rock == 'tie'){return 'tie'}
                if(this.animating && this.humanguess == 1) { return 'humanguess' }
                if(this.animating && this.computerguess == 1) { return 'computerguess' }
            },
        },

        methods: {
            inputrps(input){
                if(! this.active || this.animating) {
                    return
                }
                this.humanguess = input;
                this.computerguess = this.nextguess;
                this.active = false;
                this.animating = true;
                var home = this;
                setTimeout(function(){ home.animating = false; }, 700);
                this.sendGuess( this.calculateResult(input) );
            },

            calculateResult(guess) {
                var result = null;
                switch(guess + '' + this.nextguess) {
                    case '11':
                        result = 'a'
                        this.humanresult = "tie!"
                        this.result = 'tie';
                        this.guiresult = {rock: 'tie', paper: '', scissors: ''}
                        break;
                    case '12':
                        result = 'b'
                        this.humanresult = "lose!"
                        this.result = 'lose'
                        this.guiresult = {rock: 'lose', paper: 'win', scissors: ''}
                        this.computerwins++
                        break;
                    case '13':
                        result = 'c'
                        this.humanresult = "win!"
                        this.result = 'win'
                        this.guiresult = {rock: 'win', paper: '', scissors: 'lose'}
                        this.humanwins++
                        break;
                    case '21':
                        result = 'd'
                        this.humanresult = "win!"
                        this.result = 'win'
                        this.guiresult = {rock: 'lose', paper: 'win', scissors: ''}
                        this.humanwins++
                        break;
                    case '22':
                        result = 'e'
                        this.humanresult = "tie!"
                        this.result = 'tie'
                        this.guiresult = {rock: '', paper: 'tie', scissors: ''}
                        break;
                    case '23':
                        result = 'f'
                        this.humanresult = "lose!"
                        this.result = 'lose'
                        this.guiresult = {rock: '', paper: 'lose', scissors: 'win'}
                        this.computerwins++
                        break;
                    case '31':
                        result = 'g'
                        this.humanresult = "lose!"
                        this.result = 'lose'
                        this.guiresult = {rock: 'win', paper: '', scissors: 'lose'}
                        this.computerwins++
                        break;
                    case '32':
                        result = 'h'
                        this.humanresult = "win!"
                        this.result = 'win'
                        this.guiresult = {rock: '', paper: 'lose', scissors: 'win'}
                        this.humanwins++
                        break;
                    case '33':
                        result = 'i'
                        this.humanresult = "tie!"
                        this.result = 'tie'
                        this.guiresult = {rock: '', paper: '', scissors: 'tie'}
                        break;
                    default:
                        result = 'error'
                }
                return result
            },

            sendGuess(result) {
                var home = this;
                this.$eventBus.$emit('getresults');
                axios.patch('/api/ropasi/' + this.rps_id, {
                    guess: this.humanguess,
                    result: result,
                    nbsamplelength: this.nbsamplelength,
                    ownorall: this.ownorall,
                })
                    .then( response => {
                        var number = response.data;
                        var guide = String(number).charAt(4)
                        this.calculatedguess = Number(guide)
                        // this.calculatedguess = response.data
                        this.active = true
                        this.guessRPS();
                    } )
            },

            guessRPS() {
                if(! this.calculatedguess){
                    console.log('calculating')
                    this.nextguess = Math.floor(Math.random() * 3) + 1;
                    return
                }
                this.nextguess = this.calculatedguess;
            },

            readableRPS(guess){
                switch(guess){
                    case 1:
                        return 'rock'
                    case 2:
                        return 'paper'
                    case 3:
                        return 'scissors'
                    default:
                        return 'error'
                }
            },

            setActivechoice(choice) {
                this.nbsamplelength = choice;
            },

            setComparison(choice) {
                this.ownorall = choice;
            },
        }
    }
</script>
